pragma solidity >=0.4.22 <0.9.0;

import "../helpers/BaseStorage.sol";

import '../ContractManager.sol';
import './AdminStorage.sol';
import "../helpers/BaseController.sol";


contract AdminController is BaseController {

    function addAdmin(bytes32 _name) public returns(uint) {

        ContractManager _manager = ContractManager(managerAddr);

        address _adminStorageAddr = _manager.getAddress('AdminStorage');

        AdminStorage _adminStorage = AdminStorage(_adminStorageAddr);
        return _adminStorage.addAdmin(_name);

    }

}
