pragma solidity >=0.4.22 <0.9.0;

import "../helpers/BaseController.sol";

import '../ContractManager.sol';
import './CandidateStorage.sol';



contract CandidatesController is BaseController {

    function registerCandidate(bytes32 _name, bytes32 _party) public returns(uint) {

        ContractManager _manager = ContractManager(managerAddr);
        address _candidateStorageAddr = _manager.getAddress('CandidateStorage');

        CandidateStorage _candidateStorage = CandidateStorage(_candidateStorageAddr);
        return _candidateStorage.registerCandidate(_name, _party);
    }


}
